import React, {Component} from 'react';
import { socket } from '../socket';
import p5 from 'p5';

class Sketch extends Component{
    constructor(props)
    {
        super(props);
        this.state = {
            username: this.props.username,
            active: true,
        }
        this.myRef = React.createRef();
    }

    componentDidMount()
    {
        socket.on("receiveSketch", (data) => {
            console.log(data.from);
        });

        socket.on("receiveActive", (name) => {
            if(name == this.state.username)
            {
                this.setState({
                    active: true
                });
            }
            else
            {
                this.setState({
                    active: false
                });
            }
        });

        if(this.sketch)
        {
            this.sketch.remove();
        }

        this.sketch = new p5( p  => {

            p.setup = () => {
                // let height = window.innerHeight*(isMobile?1.2:1);
                p.createCanvas(500,500).parent(this.myRef.current);
                p.background('#ffffff');

            };

            p.draw = () => {
                p.noFill();
            }
        });
    }



    render()
    {
        let guard = (<div style={{
            position: "absolute",
            background: "rgba(0,0,255,0.5)",
            height: "100%",
            width: "100%"
        }}>
        </div>);
        return (
            <div 
            style={{
                background: "rgba(0,0,0,0.5)",
                display: "flex",
                flexDirection: "row",
                flex: 1,
                alignItems: "center",
                justifyContent: "center",
                position: "relative"
            }}>
                <div 
                style={{flex: 1}}
                ref={this.myRef}>
                </div>
                <div style={{
                    justifySelf: "flex-end",
                    float: "right"
                }}>
                    hello
                </div>
                {this.state.active?<></>:guard}
            </div>
        );
    }
}

export default Sketch;
