import React, {Component} from 'react';
import { socket } from '../socket';
import p5 from 'p5';

class EndSketch extends Component{
    constructor(props)
    {
        super(props);
        this.state = {
            username: this.props.username,
        }
        this.myRef = React.createRef();
    }

    componentDidMount()
    {

        if(this.sketch)
        {
            this.sketch.remove();
        }

        this.sketch = new p5( p  => {

            p.setup = () => {

                this.canvas = p.createCanvas(500,500*3).parent(this.myRef.current);
                p.background('#ffffff');      
            };

            p.draw = () => {

            }
        });


    }



    render()
    {
        return (
            <div style={{
                background: "var(--bg1)",
                display: "flex",
                height: '100vh',
                width: '100vw',
                overflowX: "hidden"
            }}>
            <div 
            style={{
                display: "flex",
                flexDirection: "row",
                flex: 1,
                alignItems: "center",
                justifyContent: "center",
                position: "relative",
            }}>

                <div 
                style={{
                    flex: 1,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                }}
                ref={this.myRef}>
                </div>
            </div>
        </div>
        );
    }
}

export default EndSketch;
