import React, {Component} from 'react';
import { socket } from '../socket';
import p5 from 'p5';

class EndSketch extends Component{
    constructor(props)
    {
        super(props);
        this.state = {
            username: this.props.username,
            baseStrings: []
        }
        this.myRef = React.createRef();
    }

    componentDidMount()
    {

        if(this.sketch)
        {
            this.sketch.remove();
        }

        this.sketch = new p5( p  => {

            p.setup = () => {

                this.canvas = p.createCanvas(500,500*3).parent(this.myRef.current);
                p.background('#ffffff');      
            };

            p.draw = () => {

            }
        });
    }

    imageLoader(url) {
        return new Promise( (resolve, reject) => {
            loadImage(url, (result) => {

                // Sets the image object property to the result for other methods to access
                this.imageObject = result;

                // Resolves the Promise with the result
                resolve(result);

            }, reject(Error("Image not found")));
        })
    }

    render()
    {
        return (
            <div style={{
                background: "var(--bg1)",
                display: "flex",
                height: '100vh',
                width: '100vw',
                overflowX: "hidden"
            }}>
            <div 
            style={{
                display: "flex",
                flexDirection: "row",
                flex: 1,
                alignItems: "center",
                justifyContent: "center",
                position: "relative",
            }}
            ref={this.myRef}>
            </div>
        </div>
        );
    }
}

export default EndSketch;
