import React, {Component} from 'react';
import { socket } from '../socket';
import p5 from 'p5';

class Sketch extends Component{
    constructor(props)
    {
        super(props);
        this.state = {
            username: this.props.username,
            active: true,
            brushSize: 5,
            mode: 1,
        }
        this.myRef = React.createRef();
        // this.color = "#000000";
        this.paintMode = this.paintMode.bind(this);
        this.eraseMode = this.eraseMode.bind(this);
        this.reset = this.reset.bind(this);
        this.sendSketch = this.sendSketch.bind(this);
    }

    componentDidMount()
    {
        socket.on("receiveSketch", (data) => {
            console.log(data.from);
        });

        socket.on("receiveActive", (name) => {
            if(name == this.state.username)
            {
                this.setState({
                    active: true
                });
            }
            else
            {
                this.setState({
                    active: false
                });
            }
        });

        if(this.sketch)
        {
            this.sketch.remove();
        }

        this.sketch = new p5( p  => {

            p.setup = () => {
                // let height = window.innerHeight*(isMobile?1.2:1);
                this.canvas = p.createCanvas(500,500).parent(this.myRef.current);
                p.background('#ffffff');

            };

            p.draw = () => {
                if (p.mouseIsPressed) {
                    p.stroke(this.state.mode?"#000000":"#ffffff");
                    p.strokeWeight(this.state.brushSize*this.state.brushSize);
                    p.line(p.mouseX, p.mouseY, p.pmouseX, p.pmouseY);
                }
            }
        });
    }

    paintMode()
    {
        this.setState({
            mode: 1
        });
    }

    eraseMode()
    {
        this.setState({
            mode: 0
        });
    }

    reset()
    {
        this.sketch.background(255);
    }

    sendSketch()
    {
        if(this.canvas)
        {
            console.log(this.canvas.elt.toDataURL());
        }
    }

    render()
    {
        let guard = (<div style={{
            position: "absolute",
            background: "var(--bg1)",
            height: "100%",
            width: "100%",
            opacity: 0.8
        }}>
        </div>);
        return (
            <div 
            style={{
                display: "flex",
                flexDirection: "row",
                flex: 1,
                alignItems: "center",
                justifyContent: "center",
                position: "relative"
            }}>

                <div 
                style={{
                    flex: 1,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                }}
                ref={this.myRef}>
                </div>

                <div 

                className='brushOptions'
                style={{
                    justifySelf: "flex-end",
                    float: "right",
                    height: "100vh",
                    flexDirection: "column",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    // background: "rgba(0,0,0,0.5)",
                    margin: "0.1em"
                }}>
                    <input
                    type="range"
                    min="1"
                    max="10"
                    step="0.5"
                    className='slider'
                    onChange={(e) => this.setState({ brushSize: e.target.value})}
                    value={this.state.brushSize}></input>
                    <button 
                        style={{
                        height: "2.5em",
                        background: this.state.mode?"var(--bg3)":"var(--bg2)"
                        }}
                        onClick={this.paintMode}>
                        Paint
                    </button>
                    <button 
                        style={{
                        height: "2.5em",
                        background: this.state.mode?"var(--bg2)":"var(--bg3)"
                        }}
                        onClick={this.eraseMode}>
                        Erase
                    </button>
                    <button 
                        style={{
                        height: "2.5em",
                        background: this.state.mode?"var(--bg2)":"var(--bg3)"
                        }}
                        onClick={this.reset}>
                        Reset
                    </button>
                    <button 
                        style={{
                        height: "2.5em",
                        background: this.state.mode?"var(--bg2)":"var(--bg3)"
                        }}
                        onClick={this.sendSketch}>
                        Send
                    </button>
                </div>

                {this.state.active?<></>:guard}

            </div>
        );
    }
}

export default Sketch;
