import React, {Component} from 'react';
import { socket } from '../socket';

class Sketch extends Component{
    constructor(props)
    {
        super(props);
        this.state = {
            username: this.props.username,
            active: false,
        }
    }

    componentDidMount()
    {
        socket.on("receiveSketch", (data) => {
            console.log(data.from);
            this.setState({ 
                active: true
            });
        });
    }

    render()
    {
        return (
            <div style={{
                // background: "rgba(0,0,0,0.5)",
                display: "flex",
                flex: 1,
                alignItems: "center",
                justifyContent: "center",
                position: "relative"
            }}>
                <div style={{
                    background: "#fff",
                    height: '500px',
                    width: '500px',
                }}>
                    hello
                </div>
                <div style={{
                    position: "absolute",
                    background: "rgba(0,0,255,0.5)",
                    height: "100%",
                    width: "100%"
                }}>
                </div>
            </div>
        );
    }
}

export default Sketch;
